import { createRouter, createWebHistory } from 'vue-router';
import DeviceList from '../components/DeviceList.vue';  // 确保路径正确
import DeviceDetails from '../components/DeviceDetails.vue';  // 确保路径正确

const routes = [
  {
    path: '/',
    name: 'Home',
    component: DeviceList
  },
  {
    path: '/device/:id',
    name: 'DeviceDetails',
    component: DeviceDetails,
    props: true
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
